                            <div class="col-2 d-flex align-items-center justify-content-center flex-column">
                                <div class="d-flex">
                                    <h5 class="card-title fw-bold">R$ 999</h5><small>99</small>
                                </div>
                                <small class="fw-lighter">Impostos: R$ 00,00</small>
                            </div>
